
// make it easy to pass data to an error
function Bomb(message, code, name, data, debug) {
  this.name  = name || 'Unknown Server Error';
  this.message = message || 'An unknown server error has occurred.';
  this.stack = (new Error()).stack;
  this.code  = code || 500;
  if(data) this.data = data;
  if(debug) this.debug = debug;
}
Bomb.prototype = Object.create(Error.prototype);
Bomb.prototype.constructor = Bomb;

module.exports = Bomb;