var _ = require('lodash');
var config = require('config');

var helper = {

  redirectTo : function(to) {
    return function (req, res) {
      res.redirect(to);
    }
  },

  getParams:function(req){
    return _.extend({}, req.params, req.query, req.body);
  },

  sendFile : function(filePath, req, res){
    res.sendFile(filePath, { root: config.dirs.root });
  },

  renderView : function(view, req, res){
    res.render(view, {ctx:req.ctx, config:req.tenantConfig, server:config.public });
  },

  defaultRenderer : function(view){
    return function(req, res){
      helper.renderView(view, req, res);
    }
  }

};

module.exports = helper;