
// ensure testing config...
process.env.NODE_ENV = 'testing';

var config = require('config');

function startServer(done) {
  var app = require(config.dirs.root + '/app.js');
  var server = app.listen(process.env.PORT, function (err, result) {
    if(err) return done(err);
    done();
  });
  return server;
}
function stopServer(app, done){
  app.close(function(){
    done();
  });
}

module.exports = {
  start:startServer,
  stop:stopServer
};